import { createSlice } from '@reduxjs/toolkit'
import axios from "axios";

export const compostSlice = createSlice({
  name: 'compost',
  initialState:{
    list: [],
  } ,
  reducers: {
   setComposts(state, action) {
     state.list = action.payload
   },
   addDogs(state, action) {
      state.list.push(action.payload)
    }
  },
})

export const { setDogs, addDogs } = dogSlice.actions

export const fetchDogs = () => async dispatch => {
    try{
        const response = await axios.get('http://127.0.0.1:8000/dogs');
        console.log('reponse', response.data);
        dispatch(setDogs(response.data));
        return response.data;
    }
    catch(error){
        console.log(error);
    }
}

export default dogSlice.reducer
