import '../App.css';
import React, { Component } from 'react';
import Header from '../components/header'
import Footer from '../components/footer'
import article1 from '../img/Articles/ocgBali.jpg';
import article2 from '../img/Articles/eolienne.jpg';
import article3 from '../img/Articles/burger.jpg';

class Articles extends Component {

  constructor() {
    super();
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    fetch('http://127.0.0.1:8000/api/articles')
      .then(response => response.json())
      .then(data => this.setState({ articles: data }));
  }

  render() {
    return (
      <div className='Blog'>
      <Header />
      {this.state.articles.map(article => (
        <div className="container">
          <div className="row">
            <div className="col-md-4 col-12 col-lg-4">
            <div class="card">
            <img class="card-img-top" src={ articles.image } alt="Card image cap" />
              <h1>{article.title}</h1>
              <img src={article.image} alt="article" className="img-fluid" />
              <p>{article.content}</p>
            </div>
          </div>
          </div>
        </div>
      ))}; 
      <div class="container">
              <div class="row">
                  <div class="col-md-4 col-12 col-lg-4">
                      <div class="card">
                          <img class="card-img-top" src={ articles.image } alt="Card image cap" />
                          <div class="card-body">
                              <h5 class="card-title">Card title</h5>
                              <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                              <a href="#" class="btn btn-outline-dark">Voir</a>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
          <Footer />
      </div>
    );
  }
  }
  export default Articles;
