import '../App.css';
import Header from '../components/header';
import Footer from '../components/footer';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import { Marker, Popup } from 'react-leaflet';

function Map() {

  let xmlhttp = new XMLHttpRequest();

  xmlhttp.onreadystatechange = () => {
      // La transaction est terminée ?
      if(xmlhttp.readyState == 4){
          // Si la transaction est un succès
          if(xmlhttp.status == 200){
              // On traite les données reçues
              let donnees = JSON.parse(xmlhttp.responseText)

              // On boucle sur les données (ES8)
              Object.entries(donnees.composts).forEach(compost => {
                  // Ici j'ai une seule agence
                  // On crée un marqueur pour l'agence
                  const position = [[compost[id].lat, compost[id].long]];
              })
          }else{
              console.log(xmlhttp.statusText);
          }
      }
  }

  xmlhttp.open("GET", "http://127.0.0.1:8000/compost");

  xmlhttp.send(null);



  return (
    <div className='Map'>
      <Header />
      <div class="container">
        <div class="row">
          <div class="col-md-6 col-12 col-lg-6">
            <br />
            <h1>TOUS LES COMPOSTS COLLECTIFS DE LYON :</h1>
            <MapContainer center={[45.75960601090755, 4.832381729701485]} zoom={13} scrollWheelZoom={false}>
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              <Marker position={position}>
                <Popup>
                  A pretty CSS3 popup. <br /> Easily customizable.
                </Popup>
              </Marker>
            </MapContainer>
          </div>
          <div class="col-md-6 col-12 col-lg-6">

            <h3>Sélectionner votre arrondissement :</h3>
          <div class=" d-flex align-items-center col-md-6 col-12 col-lg-6">

          <ul class="navbar-nav">
              <li class="nav-item">
                <a class="nav-link active" aria-current="page" href="#">Lyon1</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon2</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon3</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon4</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon5</a>
              </li>
              </ul>
              <ul class="navbar-nav">
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon6</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon7</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon8</a>
              </li>
              <li class="nav-item">
                <a class="nav-link active" href="#">Lyon9</a>
              </li>

              </ul>

                 </div>
                </div>
              </div>
            </div>

      <Footer />
    </div>
  );
}

export default Map;
